          if ($(e.target).is('.new')) {
            day.add(1, 'iMonth');
          }

          setValue(day.iDate(parseInt($(e.target).text(), 10)));
        } else {
          if ($(e.target).is('.old')) {
            day.subtract(1, 'months');
          }

          if ($(e.target).is('.new')) {
            day.add(1, 'months');
          }

          setValue(day.date(parseInt($(e.target).text(), 10)));
