            date,
            viewDate,
            unset = true,
            input,
            component = false,
            widget = false,
            use24Hours,
            minViewModeNumber = 0,
            actualFormat,
            parseFormats,
            currentViewMode,
            datePickerModes = [
                {
                    clsName: 'days',
                    navFnc: (options.hijri ? 'iMonth' : 'Month'),
                    navStep: 1
                },
                {
                    clsName: 'months',
                    navFnc: (options.hijri ? 'iYear' : 'y'),
                    navStep: 1
                },
                {
                    clsName: 'years',
                    navFnc: (options.hijri ? 'iYear' : 'y'),
                    navStep: 10
                },
                {
                    clsName: 'decades',
                    navFnc: (options.hijri ? 'iYear' : 'y'),
                    navStep: 100
                }
