import type { ChartType, ChartDataset, DefaultDataPoint } from 'chart.js'

import type { TChartData, TChartOptions, TypedChartJS } from './types'

import { SetupContext } from 'vue'

export enum ChartEmits {
  ChartRendered = 'chart:rendered',
  ChartUpdated = 'chart:updated',
  ChartDestroyed = 'chart:destroyed',
  LabelsUpdated = 'labels:updated'
}

export function chartCreate<
  TType extends ChartType = ChartType,
  TData = DefaultDataPoint<TType>,
  TLabel = unknown
>(
  createChartFunction: (
    data: TChartData<TType, TData, TLabel>,
    options: TChartOptions<TType>
  ) => void,
  chartData: TChartData<TType, TData, TLabel>,
  chartOptions: TChartOptions<TType>,
  context?: SetupContext
): void {
  createChartFunction(chartData, chartOptions)

  if (context !== undefined) {
    context.emit(ChartEmits.ChartRendered)
  }
}

export function chartUpdate<
  TType extends ChartType,
  TData = DefaultDataPoint<TType>,
  TLabel = unknown
>(chart: TypedChartJS<TType, TData, TLabel>, context?: SetupContext): void {
  chart.update()

  if (context !== undefined) {
    context.emit(ChartEmits.ChartUpdated)
  }
}

export function chartDestroy<
  TType extends ChartType,
  TData = DefaultDataPoint<TType>,
  TLabel = unknown
>(chart: TypedChartJS<TType, TData, TLabel>, context?: SetupContext): void {
  chart.destroy()

  if (context !== undefined) {
    context.emit(ChartEmits.ChartDestroyed)
  }
}

export function getChartData<
  TType extends ChartType = ChartType,
  TData = DefaultDataPoint<TType>,
  TLabel = unknown
>(
  data: TChartData<TType, TData, TLabel>,
  datasetIdKey: string
): TChartData<TType, TData, TLabel> {
  const nextData = {
    labels: typeof data.labels === 'undefined' ? [] : [...data.labels],
    datasets: []
  }

  setChartDatasets(nextData, { ...data }, datasetIdKey)
  return nextData
}

export function setChartDatasets<
  TType extends ChartType = ChartType,
  TData = DefaultDataPoint<TType>,
  TLabel = unknown
>(
  oldData: TChartData<TType, TData, TLabel>,
  newData: TChartData<TType, TData, TLabel>,
  datasetIdKey: string
): void {
  const addedDatasets: ChartDataset<TType, TData>[] = []

  oldData.datasets = newData.datasets.map(
    (nextDataset: Record<string, unknown>) => {
      // given the new set, find it's current match
      const currentDataset = oldData.datasets.find(
        (dataset: Record<string, unknown>) =>
          dataset[datasetIdKey] === nextDataset[datasetIdKey]
      )

      // There is no original to update, so simply add new one
      if (
        !currentDataset ||
        !nextDataset.data ||
        addedDatasets.includes(currentDataset)
      ) {
        return { ...nextDataset }
      }

      addedDatasets.push(currentDataset)

      Object.assign(currentDataset, nextDataset)

      return currentDataset
    }
  ) as ChartDataset<TType, TData>[]
}

export function setChartLabels<
  TType extends ChartType,
  TData = DefaultDataPoint<TType>,
  TLabel = unknown
>(
  chart: TypedChartJS<TType, TData, TLabel>,
  labels: TLabel[] | undefined,
  context?: SetupContext
): void {
  chart.data.labels = labels

  if (context !== undefined) {
    context.emit(ChartEmits.LabelsUpdated)
  }
}

export function compareData<
  TType extends ChartType = ChartType,
  TData = DefaultDataPoint<TType>,
  TLabel = unknown
>(
  newData: TChartData<TType, TData, TLabel>,
  oldData: TChartData<TType, TData, TLabel>
): boolean {
  // Get new and old DataSet Labels
  const newDatasetLabels = newData.datasets.map(dataset => {
    return dataset.label
  })

  const oldDatasetLabels = oldData.datasets.map(dataset => {
    return dataset.label
  })

  // Check if Labels are equal and if dataset length is equal
  return (
    oldData.datasets.length === newData.datasets.length &&
    newDatasetLabels.every((value, index) => value === oldDatasetLabels[index])
  )
}

export const templateError =
  'Please remove the <template></template> tags from your chart component. See https://vue-chartjs.org/guide/#vue-single-file-components'
import {
  Chart as ChartJS,
  BarController,
  BubbleController,
  DoughnutController,
  LineController,
  PieController,
  PolarAreaController,
  RadarController,
  ScatterController
} from 'chart.js'

import type {
  ChartType,
  ChartComponentLike,
  DefaultDataPoint,
  ChartOptions,
  Plugin
} from 'chart.js'

import {
  defineComponent,
  ref,
  shallowRef,
  h,
  onMounted,
  onBeforeUnmount,
  watch,
  isProxy,
  toRaw,
  PropType
} from 'vue'

import {
  chartCreate,
  chartDestroy,
  chartUpdate,
  getChartData,
  setChartLabels,
  setChartDatasets,
  compareData,
  templateError
} from './utils'

import type {
  TChartData,
  TChartOptions,
  TypedChartJS,
  TypedChartComponent
} from './types'

export const generateChart = <
  TType extends ChartType = ChartType,
  TData = DefaultDataPoint<TType>,
  TLabel = unknown
>(
  chartId: string,
  chartType: TType,
  chartController: ChartComponentLike
): TypedChartComponent<TType, TData, TLabel> =>
  defineComponent({
    props: {
      chartData: {
        type: Object as PropType<TChartData<TType, TData, TLabel>>,
        required: true
      },
      chartOptions: {
        type: Object as PropType<TChartOptions<TType>>,
        default: () => {}
      },
      datasetIdKey: {
        type: String,
        default: 'label'
      },
      chartId: {
        type: String,
        default: chartId
      },
      width: {
        type: Number,
        default: 400
      },
      height: {
        type: Number,
        default: 400
      },
      cssClasses: {
        type: String,
        default: ''
      },
      styles: {
        type: Object as PropType<Partial<CSSStyleDeclaration>>,
        default: () => {}
      },
      plugins: {
        type: Array as PropType<Plugin<TType>[]>,
        default: () => []
      }
    },
    setup(props, context) {
      ChartJS.register(chartController)

      const _chart = shallowRef<TypedChartJS<TType, TData, TLabel> | null>(null)
      const canvasEl = ref<HTMLCanvasElement | null>(null)

      function renderChart(
        data: TChartData<TType, TData, TLabel>,
        options: TChartOptions<TType>
      ): void {
        if (_chart.value !== null) {
          chartDestroy<TType, TData, TLabel>(toRaw(_chart.value), context)
        }

        if (canvasEl.value === null) {
          throw new Error(templateError)
        } else {
          const chartData = getChartData<TType, TData, TLabel>(
            data,
            props.datasetIdKey
          )
          const canvasEl2DContext = canvasEl.value.getContext('2d')

          if (canvasEl2DContext !== null) {
            _chart.value = new ChartJS<TType, TData, TLabel>(
              canvasEl2DContext,
              {
                type: chartType,
                data: isProxy(data) ? new Proxy(chartData, {}) : chartData,
                options,
                plugins: props.plugins
              }
            )
          }
        }
      }

      function chartDataHandler(
        newValue: TChartData<TType, TData, TLabel>,
        oldValue: TChartData<TType, TData, TLabel>
      ): void {
        const newData = isProxy(newValue) ? toRaw(newValue) : { ...newValue }
        const oldData = isProxy(oldValue) ? toRaw(oldValue) : { ...oldValue }

        if (Object.keys(oldData).length > 0) {
          const chart = toRaw(_chart.value)

          const isEqualLabelsAndDatasetsLength = compareData<
            TType,
            TData,
            TLabel
          >(newData, oldData)

          if (isEqualLabelsAndDatasetsLength && chart !== null) {
            setChartDatasets<TType, TData, TLabel>(
              chart?.data,
              newData,
              props.datasetIdKey
            )

            if (newData.labels !== undefined) {
              setChartLabels<TType, TData, TLabel>(
                chart,
                newData.labels,
                context
              )
            }

            chartUpdate<TType, TData, TLabel>(chart, context)
          } else {
            if (chart !== null) {
              chartDestroy<TType, TData, TLabel>(chart, context)
            }

            chartCreate<TType, TData, TLabel>(
              renderChart,
              props.chartData,
              props.chartOptions as ChartOptions<TType>,
              context
            )
          }
        } else {
          if (_chart.value !== null) {
            chartDestroy<TType, TData, TLabel>(toRaw(_chart.value), context)
          }

          chartCreate<TType, TData, TLabel>(
            renderChart,
            props.chartData,
            props.chartOptions as ChartOptions<TType>,
            context
          )
        }
      }

      watch(
        () => props.chartData,
        (
          newValue: TChartData<TType, TData, TLabel>,
          oldValue: TChartData<TType, TData, TLabel>
        ) => chartDataHandler(newValue, oldValue),
        { deep: true }
      )

      onMounted(() => {
        if (
          'datasets' in props.chartData &&
          props.chartData.datasets.length > 0
        ) {
          chartCreate<TType, TData, TLabel>(
            renderChart,
            props.chartData,
            props.chartOptions as ChartOptions<TType>,
            context
          )
        }
      })

      onBeforeUnmount(() => {
        if (_chart.value !== null) {
          chartDestroy(toRaw(_chart.value), context)
        }
      })

      return () =>
        h('div', { style: props.styles, class: props.cssClasses }, [
          h('canvas', {
            id: props.chartId,
            width: props.width,
            height: props.height,
            ref: canvasEl
          })
        ])
    }
  }) as any

export const Bar = /* #__PURE__ */ generateChart(
  'bar-chart',
  'bar',
  BarController
)

export const Doughnut = /* #__PURE__ */ generateChart(
  'doughnut-chart',
  'doughnut',
  DoughnutController
)

export const Line = /* #__PURE__ */ generateChart(
  'line-chart',
  'line',
  LineController
)

export const Pie = /* #__PURE__ */ generateChart(
  'pie-chart',
  'pie',
  PieController
)

export const PolarArea = /* #__PURE__ */ generateChart(
  'polar-chart',
  'polarArea',
  PolarAreaController
)

export const Radar = /* #__PURE__ */ generateChart(
  'radar-chart',
  'radar',
  RadarController
)

export const Bubble = /* #__PURE__ */ generateChart(
  'bubble-chart',
  'bubble',
  BubbleController
)

export const Scatter = /* #__PURE__ */ generateChart(
  'scatter-chart',
  'scatter',
  ScatterController
)

export default {
  Bar,
  Doughnut,
  Line,
  Pie,
  PolarArea,
  Radar,
  Bubble,
  Scatter
}
